        //     <div className="desc">
        //         <h5>Title: {props.title}</h5>
        //         <h5>Author: {props.authors}</h5>
        //         <h5>Published Date: {props.publishedDate}</h5>
        //         {props.quantity == 0 ?
        //             <div>
        //                 <h5>OUT OF STOCK</h5> 
        //                 <button className="btn btn-success" onClick={ () => editBook(props.book)}>Manage Inventory</button>
        //                 <button className="btn btn-danger" onClick={ () => deleteBook(props.googleId)}>Remove Books</button>
        //             </div>
        //             : null}
        //         {props.quantity > 0 ?
        //             <div>
        //                 <h5>Available Qty: {props.quantity}</h5> 
        //                 <button className="btn btn-success" onClick={ () => editBook(props.book)}>Manage Inventory</button>
        //                 <button className="btn btn-danger" onClick={ () => deleteBook(props.googleId)}>Remove Books</button>
        //             </div>
        //             : null}
        //         {props.quantity < 0 ?
        //         <div>
        //             <h5>NOT AVAILABLE</h5> 
        //             <button className="btn btn-success" onClick={ () => editBook(props.book)}>Add Book</button>
        //         </div>
        //         : null}

        //     </div>
        //     <hr/>
        // </div>

        <div>
            <Card style={{ width: '18rem' }} key={props.key} className="box">
            <Card.Img variant="top" src={props.imageUrl} />
            <Card.Body>
                <Card.Title>{props.title}</Card.Title>
                <Card.Text>
                    Authors: {props.authors}
                    <br></br>
                    Published Date:{props.publishedDate}
                    <br></br>
                    Available Qty: {props.quantity}
                </Card.Text>
                {props.quantity == 0 ?
                    <div>
                        <h5>OUT OF STOCK</h5> 
                        <Button variant="primary" onClick={ () => editBook(props.book)}>Manage Inventory</Button>
                        <br/><br/>
                        <Button className="btn btn-danger" onClick={ () => deleteBook(props.googleId)}>Remove Books</Button>
                    </div>
                    : null}
                {props.quantity > 0 ?
                    <div>
                        <h5>Available Qty: {props.quantity}</h5> 
                        <Button variant="primary" onClick={ () => editBook(props.book)}>Manage Inventory</Button>
                        <br/><br/>
                        <Button className="btn btn-danger" onClick={ () => deleteBook(props.googleId)}>Remove Books</Button>
                    </div>
                    : null}
                {props.quantity < 0 ?
                <div>
                    <h5>NOT AVAILABLE</h5> 
                    <Button className="btn btn-success" onClick={ () => editBook(props.book)}>Add Book</Button>
                </div>
                : null}
            </Card.Body>
            </Card>
        </div>

    )
}

export default BookCard
