import React from 'react'
import {useState} from 'react';
import BookCard from './BookCard';
import Form from './Form';
import {Card, Button} from 'react-bootstrap';
// import { Container, Row, Col } from 'reactstrap';

function BookList(props) {

    const [editedBook, setEditedBook] = useState(null)

    const editBook = (book) => {
        setEditedBook(book)
    }

    const renderCard = (card, index) => {
        return (
            <Card style={{ width: '18rem' }} key={props.key} className="box">
            <Card.Img variant="top" src={props.imageUrl} />
            <Card.Body>
                <Card.Title>{props.title}</Card.Title>
                <Card.Text>
                    Authors: {props.authors}
                    <br></br>
                    Published Date:{props.publishedDate}
                    <br></br>
                    Available Qty: {props.quantity}
                </Card.Text>
                {props.quantity == 0 ?
                    <div>
                        <h5>OUT OF STOCK</h5> 
                        <Button variant="primary" onClick={ () => editBook(props.book)}>Manage Inventory</Button>
                        <br/><br/>
                        <Button className="btn btn-danger" onClick={ () => deleteBook(props.googleId)}>Remove Books</Button>
                    </div>
                    : null}
                {props.quantity > 0 ?
                    <div>
                        <h5>Available Qty: {props.quantity}</h5> 
                        <Button variant="primary" onClick={ () => editBook(props.book)}>Manage Inventory</Button>
                        <br/><br/>
                        <Button className="btn btn-danger" onClick={ () => deleteBook(props.googleId)}>Remove Books</Button>
                    </div>
                    : null}
                {props.quantity < 0 ?
                <div>
                    <h5>NOT AVAILABLE</h5> 
                    <Button className="btn btn-success" onClick={ () => editBook(props.book)}>Add Book</Button>
                </div>
                : null}
            </Card.Body>
            </Card>
        )
    }

    return (
        <div className="grid">
            {editedBook ? <Form book={editedBook} updatedBook={props.updatedBook} insertedBook={props.insertedBook}/> : null }
            <hr/>
            {
                props.books.map((book,i) => {
                    return <BookCard
                        key={i}
                        googleId={book.googleId}
                        imageUrl={book.imageUrl}
                        title={book.title}
                        authors={book.authors}
                        publishedDate={book.publishedDate}
                        quantity={book.quantity}

                        deleteBook={props.deleteBook}
                        editBook={editBook}
                        book={book}
                        />

                })
            }

        </div>


    )
}

export default BookList
